"use strict";var express=require("express"),bodyParser=require("body-parser"),app=express(),port=3e3,pug=require("pug");app.set("view engine","pug");var users=[{id:1,name:"Hung"},{id:2,name:"Dung"},{id:3,name:"Uyen"}];app.use(bodyParser.urlencoded({extended:!0})),app.use(bodyParser.json()),app.get("/",function(e,r){r.render("index",{title:"Welcome"})}),app.get("/users",function(e,r){r.render("./users/index",{title:"Users",users:users})}),app.get("/users/search",function(r,e){var s=users.filter(function(e){return-1!==e.name.toLowerCase().indexOf(r.query.name)}),n=JSON.stringify(r.query.name);e.render("./users/index",{title:"Users",users:s,input:n})}),app.get("/users/create",function(e,r){r.render("./users/newUser/newUser")}),app.post("/users/create",function(e,r){users.push(e.body),r.redirect("/users")}),app.listen(port,function(){return console.log("Example app listening at http://localhost:".concat(port))});